
function add(n1, n2) {
    let addResult = n1 + n2;
    return addResult;
}

function subtract(n1, n2) {
    let subResult = n1 - n2;
    return subResult;
}

function multiply(n1, n2) {
    let multiResult = n1 * n2;
    return multiResult;
}

function divide(n1, n2) {
    let divResult = n1 / n2;
    return divResult;
}

export {add, subtract, multiply, divide};